import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useForm, useFieldArray } from "react-hook-form";
import { useTheme } from "@mui/material/styles"
import { Box, TextField, Typography, Button, IconButton, Grid } from "@mui/material";
import SaveIcon from '@mui/icons-material/Save';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import DeleteIcon from '@mui/icons-material/Delete';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
    const theme = useTheme();
    const navigate = useNavigate();
    const [profileData, setProfileData] = useState(null);

    const { register, control, handleSubmit, formState: { errors }, setValue } = useForm({
        defaultValues: {
            name: '',
            age: '',
            nhsNumber: '',
            problemDescription: '',
            desiredValues: [{ name: '', value: '' }]
        }
    });
    const { fields, append, remove } = useFieldArray({
        control,
        name: "desiredValues"
    });

    useEffect(() => {
        axios({
            method: "GET",
            url: "http://localhost:5000/profile",
        })
        .then((response) => {
            const res = response.data;
            setProfileData({
                name: res.name,
                age: res.age,
                nhsNumber: res.nhsNumber,
                problemDescription: res.problemDescription,
                desiredValues: res.desiredValues,
            });
            // Setting form values
            setValue('name', res.name);
            setValue('age', res.age);
            setValue('nhsNumber', res.nhsNumber);
            setValue('problemDescription', res.problemDescription);
            if (res.desiredValues && res.desiredValues.length > 0) {
                res.desiredValues.forEach((value, index) => {
                    append({ name: value.name, value: value.value });
                });
            }
        })
        .catch((error) => {
            console.log(error);
        });
    }, [append, setValue]);

    const onSubmit = (data) => console.log(data);

    const handleExit = () => {
        navigate('/');
    };

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                margin: theme.spacing(4),
                padding: theme.spacing(3),
                backgroundColor: '#fff',
                borderRadius: theme.shape.borderRadius,
                boxShadow: theme.shadows[2]
            }}
        >
            {/* ... existing JSX ... */}
            <form onSubmit={handleSubmit(onSubmit)}>
                {/* ... form fields ... */}
            </form>
        </Box>
    );
}

export default Dashboard;
