import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useForm, useFieldArray } from "react-hook-form";
import { useTheme } from "@mui/material/styles";
import { Box, TextField, Typography, Button, IconButton, Grid } from "@mui/material";
import SaveIcon from '@mui/icons-material/Save';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import DeleteIcon from '@mui/icons-material/Delete';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
    const theme = useTheme();
    const navigate = useNavigate();
    const { register, control, handleSubmit, formState: { errors }, setValue } = useForm({
        defaultValues: {
            name: '',
            age: '',
            nhsNumber: '',
            problemDescription: '',
            desiredValues: [{ name: '', value: '' }]
        }
    });

    const { fields, append, remove } = useFieldArray({
        control,
        name: "desiredValues"
    });

    useEffect(() => {
        axios.get("http://localhost:5000/profile")
            .then(({ data }) => {
                setValue('name', data.name);
                setValue('age', data.age);
                setValue('nhsNumber', data.nhsNumber);
                setValue('problemDescription', data.problemDescription);
                if (data.desiredValues && data.desiredValues.length > 0) {
                    setValue('desiredValues', data.desiredValues);
                } else {
                    remove();
                }
            })
            .catch((error) => {
                console.log(error);
            });
    }, [setValue, remove]);

    const onSubmit = (data) => {
        axios.post("http://localhost:5000/profile", data)
            .then(response => {
                console.log("Profile updated", response.data);
            })
            .catch(error => {
                console.error("Error updating profile:", error);
            });
    };

    const handleExit = () => {
        navigate('/');
    };

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                margin: theme.spacing(4),
                padding: theme.spacing(3),
                backgroundColor: '#fff',
                borderRadius: theme.shape.borderRadius,
                boxShadow: theme.shadows[2]
            }}
        >
            <form onSubmit={handleSubmit(onSubmit)}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: theme.spacing(2) }}>
                    <Typography variant="h4" sx={{ fontWeight: 'bold' }}>UPDATE PATIENT</Typography>
                    <IconButton color="primary" aria-label="add field" onClick={() => append({ name: '', value: '' })}>
                        <AddCircleOutlineIcon />
                    </IconButton>
                    <Button variant="contained" endIcon={<ExitToAppIcon />} onClick={handleExit}>Exit</Button>
                </Box>

                <Grid container spacing={2}>
                    {/* Static fields */}
                    <Grid item xs={12} sm={6}>
                        <TextField fullWidth label="Name" variant="outlined" margin="normal" {...register("name", { required: true })} error={!!errors.name} helperText={errors.name ? "Name is required" : ""} />
                        <TextField fullWidth label="Age" type="number" variant="outlined" margin="normal" {...register("age", { required: true })} error={!!errors.age} helperText={errors.age ? "Age is required" : ""} />
                        <TextField fullWidth label="NHS Number" variant="outlined" margin="normal" {...register("nhsNumber", { required: true })} error={!!errors.nhsNumber} helperText={errors.nhsNumber ? "NHS Number is required" : ""} />
                        <TextField fullWidth label="Problem Description" multiline rows={4} variant="outlined" margin="normal" {...register("problemDescription", { required: true })} error={!!errors.problemDescription} helperText={errors.problemDescription ? "Problem Description is required" : ""} />
                    </Grid>
                    {/* Dynamic fields */}
                    <Grid item xs={12} sm={6}>
                        {fields.map((item, index) => (
                            <Box key={item.id} sx={{ display: 'flex', alignItems: 'center', marginBottom: theme.spacing(2) }}>
                                <TextField label={`Value ${index + 1} Name`} variant="outlined" sx={{ mr: 1, flex: 1 }} {...register(`desiredValues.${index}.name`)} />
                                <TextField label={`Value ${index + 1} Value`} variant="outlined" sx={{ mr: 1, flex: 1 }} {...register(`desiredValues.${index}.value`)} />
                                <IconButton aria-label="delete" onClick={() => remove(index)}><DeleteIcon /></IconButton>
                            </Box>
                        ))}
                    </Grid>
                </Grid>

                <Box sx={{ display: 'flex', justifyContent: 'flex-start', gap: theme.spacing(2), marginTop: theme.spacing(2) }}>
                    <Button variant="contained" color="primary" startIcon={<SaveIcon />} type="submit">Save</Button>
                </Box>
            </form>
        </Box>
    );
}

export default Dashboard;
